import React, { useState, useEffect } from 'react'
import { PlusCircle, Trash2 } from 'lucide-react'

interface Note {
  id: number
  content: string
}

function App() {
  const [notes, setNotes] = useState<Note[]>([])
  const [newNote, setNewNote] = useState('')

  useEffect(() => {
    const savedNotes = localStorage.getItem('notes')
    if (savedNotes) {
      setNotes(JSON.parse(savedNotes))
    }
  }, [])

  useEffect(() => {
    localStorage.setItem('notes', JSON.stringify(notes))
  }, [notes])

  const addNote = () => {
    if (newNote.trim() !== '') {
      setNotes([...notes, { id: Date.now(), content: newNote }])
      setNewNote('')
    }
  }

  const deleteNote = (id: number) => {
    setNotes(notes.filter(note => note.id !== id))
  }

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-400 via-pink-500 to-red-500 flex flex-col items-center justify-center p-4">
      <h1 className="text-4xl font-bold text-white mb-8">Beautiful Notes</h1>
      <div className="w-full max-w-md bg-white rounded-lg shadow-lg p-6">
        <div className="flex mb-4">
          <input
            type="text"
            value={newNote}
            onChange={(e) => setNewNote(e.target.value)}
            placeholder="Enter a new note..."
            className="flex-grow p-2 border border-gray-300 rounded-l-md focus:outline-none focus:ring-2 focus:ring-purple-600"
          />
          <button
            onClick={addNote}
            className="bg-purple-600 text-white p-2 rounded-r-md hover:bg-purple-700 focus:outline-none focus:ring-2 focus:ring-purple-600"
          >
            <PlusCircle size={24} />
          </button>
        </div>
        <ul className="space-y-2">
          {notes.map((note) => (
            <li key={note.id} className="flex items-center justify-between bg-gray-100 p-3 rounded-md">
              <span className="flex-grow">{note.content}</span>
              <button
                onClick={() => deleteNote(note.id)}
                className="text-red-600 hover:text-red-800 focus:outline-none"
              >
                <Trash2 size={20} />
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  )
}

export default App
